      s.translate(
        100 * s.cos(s.millis() * .001 * s.PI),
        100 * s.sin(s.millis() * .001 * s.PI),
      );
      if (s.random(0, 1) &lt; .1) {
        s.fill(s.random(0, 255));
      }
      s.circle(250, 250, 100);
    };

  }
)


















